    public void writerFle(String fileName, String data) {
        File file = new File(fileName);

        FileWriter fw = null;
        try{
            fw = new FileWriter(file);
            fw.write(data);
            fw.flush();//刷新
        }catch (Exception e){
            e.printStackTrace();
        }finally{
            //4.关闭流
            if(fw != null){
                try{
                    fw.close();
                }catch(Exception e){
                    e.printStackTrace();
                }
            }
        }
    }


